<script lang="ts">
    import { onMount } from "svelte";
    import { fade } from "svelte/transition";

    let pageData: {
        text_items: {
            loc: number;
            text: string;
        }[];
        image_items: {
            loc: number;
            image: string;
        }[];
    }

    function updateHeader() {
        const header = document.getElementsByTagName("header")[0];
        if (!header) return;

        let headerBackgroundScroll = 0;
        if (window.innerWidth > 1400) headerBackgroundScroll = 520;

        if (window.scrollY > headerBackgroundScroll) {
            header.style.backgroundColor = "#1b4584";
        } else {
            header.style.backgroundColor = "transparent";
        }
    }

    onMount(() => {
        addEventListener("scroll", () => {
            updateHeader()
        });

        const userLang = navigator.language;
        const lang = userLang.split("-")[0];
        const flag = flags.filter(flag => flag.value == lang)[0];
        changeLanguage(flag);
    });

    function getText(loc: number, elementType: "text_items") {
        return pageData[elementType].filter(item => item.loc == loc)[0].text;
    }

    function getImage(loc: number, elementType: "image_items") {
        return pageData[elementType].filter(item => item.loc == loc)[0].image;
    }

    const flags = [
        {
            name: "English",
            value: "en",
            icon: "/flags/gb.svg",
        },
        {
            name: "Deutsch",
            value: "de",
            icon: "/flags/de.svg",
        },
        {
            name: "Français",
            value: "fr",
            icon: "/flags/fr.svg",
        },
        {
            name: "Nederlands",
            value: "nl",
            icon: "/flags/nl.svg",
        },
        {
            name: "Português",
            value: "pt-pt",
            icon: "/flags/pt.svg",
        },
    ];

    let selected = flags[0];

    function changeLanguage(flag: { name: string; value: string; icon: string; }) {
        selected = flag;

        const options = {
            method: 'POST',
            body: `{"target_lang":"${selected.value.toLocaleUpperCase()}"}`,
            mode: "cors"
        };

        let finished = false;

        setTimeout(() => {
            if (!finished) document.body.style.filter = "blur(5px)";
        }, 250);

        fetch('http://178.62.220.8:8080/content', options)
            .then(async response => response.json())
            .then(data => {
                pageData = data;
                finished = true;
                document.body.style.filter = "none";
                updateHeader();
            })
            .catch(err => console.error(err));
    }
</script>

<header>
    <img id="logo" src="./logo.svg" />

    <div id="select-container">
        <ul>
            <li tooltip={selected.name}>
                <button><img src={selected.icon} alt={selected.name} /></button>
            </li>
            {#each flags as flag}
                {#if selected.value !== flag.value}
                    <li tooltip={flag.name}>
                        <button on:click={() => {
                            changeLanguage(flag);
                        }}><img src={flag.icon} alt={flag.name} /></button>
                    </li>
                {/if}
            {/each}
        </ul>
    </div>
</header>

{#key pageData}
