<style lang="scss">
    @import url('https://fonts.googleapis.com/css2?family=Mukta:wght@400;700&family=Montserrat:wght@400;600;700&family=Outfit:wght@400;600;700&display=swap');

    :global(body) {
        margin: 0;
        font-family: Arial, sans-serif;
    }

    body {
        height: 120vh;
        max-width: 1024px;
        margin: 0 auto;
        font-family: "Mukta", Sans-serif;
    }

    header {
        height: 100px;
        display: flex;
        justify-content: space-between;
        align-items: center;
        position: fixed;
        z-index: 1000;
        transition: background-color 0.25s ease-in-out;
        width: 100%;
        margin: 0 auto;
        min-width: 350px;
        background-color: #1b4584;

        #logo {
            position: absolute;
            left: 25px;
            top: 25px;
            height: 40px;
        }

        #select-container {
            position: absolute;
            width: 66px;
            height: 40px;
            background: #ffffff;
            top: 25px;
            right: 0;
            transform: translateX(-50%);
            border-radius: 10px;
            border: 0.5px solid rgba(202, 219, 226, 0.4);
            box-shadow: 0px 3px 6px rgba(79, 104, 113, 0.2);
            overflow: hidden;
            transition: height 0.2s ease-in-out,
            border-radius 0.2s ease-in-out,
            box-shadow 0.2s ease-in-out;

            &:hover {
                height: 202px;
                border-radius: 20px;
                box-shadow: 0px 7px 10px rgba(79, 104, 113, 0.3);
            }

            ul {
                list-style-type: none;
                position: relative;

                li {
                    opacity: 1;
                    transition: opacity 0.2s ease-in-out;
                    
                    &:first-child {
                        img {
                            cursor: default;
                        }
                    }
                    
                    img {
                        width: 25px;
                        height: 25px;

                        display: block;
                        left: 50%;
                        transform: translate(-75%, -40%);
                        margin-bottom: 8px;

                        border-radius: 50%;
                        border: 2px solid #ffffff;
                        box-shadow: 0px 0px 6px rgba(79, 104, 113, 0.3);

                        cursor: pointer;
                        transition: all 0.1s ease-in-out;
                        
                        &:hover {
                            box-shadow: 0px 5px 10px rgba(79, 104, 113, 0.3);
                            transform: translate(-75%, -40%) scale(1.08);
                        }
                    }

                    button {
                        background: none;
                        border: none;
                        cursor: pointer;
                        margin: 0;
                        padding: 0;
                    }
                }
            }
        }

        [tooltip] {
            position: relative;
            font-weight: bold;
        }

        [tooltip]::before,
        [tooltip]::after {
            text-transform: none;
            font-size: 12px;
            line-height: 1;
            user-select: none;
            pointer-events: none;
            position: absolute;
            display: none;
            opacity: 0;
        }

        [tooltip]::before {
            content: "";
            border: 5px solid transparent;
            z-index: 1001;
        }

        [tooltip]::after {
            content: attr(tooltip);

            font-family: Helvetica, sans-serif;
            text-align: center;

            white-space: nowrap;
            padding: 3px 0px;
            border-radius: 0.3ch;
            box-shadow: 0 1em 2em -0.5em rgba(0, 0, 0, 0.35);
            background: #465663;
            color: #fff;
            z-index: 1000;
        }

        [tooltip]:hover::before,
        [tooltip]:hover::after {
            display: block;
        }

        [tooltip=""]::before,
        [tooltip=""]::after {
            display: none !important;
        }

        [tooltip]:not([flow])::before {
            bottom: 50%;
            border-bottom-width: 0;
            border-top-color: #465663;
        }

        [tooltip]:not([flow])::after {
            bottom: calc(50% + 5px);
        }

        [tooltip]:not([flow])::before,
        [tooltip]:not([flow])::after {
            left: -15.5px;
            bottom: 40px;
            transform: translate(-35%, -0.5em);
        }

        @keyframes tooltips-vert {
            to {
                opacity: 0.9;
                transform: translate(-35%, 0);
            }
        }

        [tooltip]:not([flow]):hover::before,
        [tooltip]:not([flow]):hover::after {
            animation: tooltips-vert 300ms ease-out forwards;
        }
    }

    #main {
        display: flex;
        justify-content: space-between;
        color: #ffffff;
        padding-top: 180px;
        max-height: 410px;
        background-image: linear-gradient(245deg, #02010100 43%, #1b4584 43.07%);

        #info {
            width: 53%;
            padding-bottom: 64px;

            h1 {
                font-size: 35px;
                line-height: 1.2em;
                margin: 0;
                color: #eb5e25;
                margin-bottom: .6rem;
                font-family: "Mukta", Sans-serif;
            }

            h2 {
                font-size: 25px;
                font-weight: 600;
                margin: 0;
                margin-bottom: 30px;
                line-height: 1.2em;
                font-family: "Mukta", Sans-serif;
            }

            p {
                font-size: 1rem;
                margin-bottom: 20px;
                line-height: 29px;
                font-family: "Montserrat", Sans-serif
            }

            button {
                background-color: #eb5e25;
                color: #ffffff;
                padding: 8px 30px;
                line-height: 2rem;
                border: none;
                border-radius: 50px;
                font-weight: 400;
                font-size: 1rem;
                margin-top: 20px;
                cursor: pointer;
                transition: all 0.2s ease-in-out;
                font-family: "Outfit", Sans-serif;
                text-transform: uppercase;
                font-weight: 400;

                &:hover {
                    background-color: #ffffff;
                    color: #1b4584;
                }
            }
        }

        img {
            position: absolute;
            right: 0;
            top: 0;
            width: 60vw;
            height: 100%;
            max-height: 620px;
            z-index: -1;
            object-fit: cover;
        }

        #filler {
            position: absolute;
            left: 0;
            top: 0;
            height: 100%;
            max-height: 620px;
            width: 40vw;
            z-index: -1;
            background-color: #1b4584;
        }

        @media screen and (max-width: 900px) {
            padding-top: 100px;
            max-height: none;
            background-image: linear-gradient(245deg, #1b4584 43%, #1b4584 43.07%);

            #info {
                width: 100%;
                padding-bottom: 0;
            }

            #filler {
                width: 100%;
                height: 50%;
            }
        }
    }

    #values {
        display: flex;
        justify-content: space-around;
        align-items: center;

        #info {
            background-color: #ffffff;
            padding: 30px;
            padding-bottom: 64px;

            h2 {
                color: #eb5e25;
                font-size: 35px;
                font-weight: 600;
                margin: 0;
                line-height: 1.2em;
                font-family: "Mukta", Sans-serif;
            }

            p {
                font-size: 1rem;
                line-height: 29px;
